// ========================================
// GESTION INTERSECTION - 3 MODES
// ========================================

var robots = global.get('robots') || {};
var mode = global.get('mode') || 'feu';
var zoneConflit = global.get('zoneConflit') || { occupee: false, robot: null };
var listePresence = global.get('listePresence') || [];

var data = msg.payload;
var topic = msg.topic;
var parts = topic.split('/');
if (parts.length < 2) return null;
var robotId = parts[1];

var isDistance = topic.includes('/distance');
var isPosition = topic.includes('/position');

// ========== MISE √Ä JOUR DES INFOS ==========
if (isDistance) {
    robots[robotId] = robots[robotId] || {};
    robots[robotId].distance = parseFloat(data);
    robots[robotId].lastUpdate = Date.now();
    global.set('robots', robots);

    if (mode === 'peloton') {
        return traiterPeloton();
    }
    if (mode === 'cooperatif') {
        return traiterCooperatif();
    }
    return null;
}

if (isPosition && typeof data === 'string') {
    if (data.startsWith('ENTREE')) {
        var infos = data.split(':');
        robots[robotId] = robots[robotId] || {};
        robots[robotId].zone = 'stockage';
        robots[robotId].voie = infos[1] === 'GAUCHE' ? 'A' : 'B';
        robots[robotId].distance = infos[2] ? parseFloat(infos[2]) : 0;
        robots[robotId].timestamp = Date.now();
        robots[robotId].actif = true;

        if (!listePresence.includes(robotId)) {
            listePresence.push(robotId);
            global.set('listePresence', listePresence);
        }
        node.status({ fill: 'blue', shape: 'dot', text: robotId + ' entr√©e voie ' + robots[robotId].voie });

    } else if (data === 'SORTIE') {
        if (zoneConflit.robot === robotId) {
            zoneConflit.occupee = false;
            zoneConflit.robot = null;
            global.set('zoneConflit', zoneConflit);
        }
        if (robots[robotId]) {
            robots[robotId].zone = 'sortie';
            robots[robotId].actif = false;
        }
        listePresence = listePresence.filter(r => r !== robotId);
        global.set('listePresence', listePresence);
        node.status({ fill: 'green', shape: 'dot', text: robotId + ' sorti' });
    } else if (data.includes('OBSTACLE')) {
        if (robots[robotId]) {
            robots[robotId].obstacle = true;
        }
        node.status({ fill: 'red', shape: 'dot', text: robotId + ' OBSTACLE' });
    }
}

global.set('robots', robots);

var ordres = [];

// ========== MODE FEU ==========
if (mode === 'feu') {
    return null;
}

// ========== MODE COOPERATIF ==========
else if (mode === 'cooperatif') {
    return traiterCooperatif();
}

// ========== MODE PELOTON ==========
else if (mode === 'peloton') {
    return traiterPeloton();
}

// Fonction coop√©ratif
function traiterCooperatif() {
    var ordresCoop = [];
    var robotsEnAttente = Object.keys(robots).filter(r => 
        robots[r].zone === 'stockage' && robots[r].actif
    );

    if (!zoneConflit.occupee && robotsEnAttente.length > 0) {
        robotsEnAttente.sort((a, b) => (robots[b].distance || 0) - (robots[a].distance || 0));
        var plusProche = robotsEnAttente[0];

        zoneConflit.occupee = true;
        zoneConflit.robot = plusProche;
        robots[plusProche].zone = 'conflit';
        global.set('zoneConflit', zoneConflit);
        global.set('robots', robots);

        ordresCoop.push({ robot: plusProche, ordre: 'GO' });
        robotsEnAttente.slice(1).forEach(r => ordresCoop.push({ robot: r, ordre: 'STOP' }));
        node.status({ fill: 'green', shape: 'dot', text: plusProche + ' GO (plus proche)' });

    } else if (zoneConflit.occupee) {
        robotsEnAttente.filter(r => r !== zoneConflit.robot).forEach(r => {
            ordresCoop.push({ robot: r, ordre: 'STOP' });
        });
        if (zoneConflit.robot && robots[zoneConflit.robot]) {
            ordresCoop.push({ robot: zoneConflit.robot, ordre: 'GO' });
        }
    }

    if (ordresCoop.length > 0) {
        return [ordresCoop.map(o => ({ payload: o }))];
    }
    return null;
}

// Fonction peloton
function traiterPeloton() {
    var DISTANCE_SECURITE = 200;
    var ordresPeloton = [];

    var robotsActifs = listePresence.filter(r => 
        robots[r] && robots[r].actif && 
        (robots[r].zone === 'stockage' || robots[r].zone === 'conflit')
    );

    if (robotsActifs.length === 0) return null;

    var leader = robotsActifs[0];

    if (!zoneConflit.occupee) {
        zoneConflit.occupee = true;
        zoneConflit.robot = leader;
        robots[leader].zone = 'conflit';
        global.set('zoneConflit', zoneConflit);
        global.set('robots', robots);
    }

    ordresPeloton.push({ robot: leader, ordre: 'GO' });

    for (var i = 1; i < robotsActifs.length; i++) {
        var robotDevant = robotsActifs[i - 1];
        var distanceRobotDevant = robots[robotDevant].distance || 0;
        ordresPeloton.push({ 
            robot: robotsActifs[i], 
            ordre: 'FOLLOW:' + distanceRobotDevant
        });
    }

    node.status({ fill: 'yellow', shape: 'dot', text: 'Peloton: ' + robotsActifs.length + ' robots' });

    if (ordresPeloton.length > 0) {
        return [ordresPeloton.map(o => ({ payload: o }))];
    }
    return null;
}

if (ordres.length > 0) {
    return [ordres.map(o => ({ payload: o }))];
}
return null;

if (!msg.payload || !msg.payload.robot) return null;

var ordre = msg.payload;
msg.topic = 'controleur/' + ordre.robot + '/ordre';
msg.payload = ordre.ordre;

node.status({ fill: 'green', shape: 'dot', text: ordre.robot + ': ' + ordre.ordre });

return msg;

var mode = global.get('mode') || 'feu';
if (mode !== 'feu') return null;

var robots = global.get('robots') || {};
var phase = global.get('phase') || 'A';

var robotsVoieA = Object.keys(robots).filter(r => robots[r].voie === 'A' && robots[r].actif);
var robotsVoieB = Object.keys(robots).filter(r => robots[r].voie === 'B' && robots[r].actif);

var ordres = [];

if (phase === 'A') {
    robotsVoieA.forEach(r => ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'GO' }));
    robotsVoieB.forEach(r => ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'STOP' }));
    global.set('phase', 'ROUGE_AB');
    node.status({ fill: 'green', shape: 'dot', text: 'Phase A (GAUCHE) - ' + robotsVoieA.length + ' robots' });

} else if (phase === 'ROUGE_AB') {
    Object.keys(robots).filter(r => robots[r].actif).forEach(r => {
        ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'STOP' });
    });
    global.set('phase', 'B');
    node.status({ fill: 'red', shape: 'dot', text: 'Rouge int√©gral' });

} else if (phase === 'B') {
    robotsVoieB.forEach(r => ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'GO' }));
    robotsVoieA.forEach(r => ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'STOP' }));
    global.set('phase', 'ROUGE_BA');
    node.status({ fill: 'yellow', shape: 'dot', text: 'Phase B (DROITE) - ' + robotsVoieB.length + ' robots' });

} else {
    Object.keys(robots).filter(r => robots[r].actif).forEach(r => {
        ordres.push({ topic: 'controleur/' + r + '/ordre', payload: 'STOP' });
    });
    global.set('phase', 'A');
    node.status({ fill: 'red', shape: 'dot', text: 'Rouge int√©gral' });
}

if (ordres.length > 0) {
    return [ordres];
}
return null;

global.set('mode', msg.payload);
global.set('robots', {});
global.set('zoneConflit', { occupee: false, robot: null });
global.set('phase', 'A');
global.set('listePresence', []);
node.status({ fill: 'green', shape: 'dot', text: 'Mode: ' + msg.payload });
return msg;

var robots = global.get('robots') || {};
var zone = global.get('zoneConflit') || {};
var mode = global.get('mode') || 'feu';
var phase = global.get('phase') || 'A';

var nbRobots = Object.keys(robots).length;
var nbActifs = Object.keys(robots).filter(r => robots[r].actif).length;

node.status({ fill: 'blue', shape: 'dot', text: nbActifs + '/' + nbRobots + ' robots actifs' });

msg.payload = {
    mode: mode,
    phase: phase,
    totalRobots: nbRobots,
    robotsActifs: nbActifs,
    robots: robots,
    zoneConflit: zone
};

return msg;

global.set('robots', {});
global.set('zoneConflit', { occupee: false, robot: null });
global.set('phase', 'A');
global.set('listePresence', []);
node.status({ fill: 'yellow', shape: 'ring', text: 'Reset effectu√©' });
msg.payload = 'Tous les robots r√©initialis√©s';
return msg;

global.set('mode', msg.payload);
global.set('robots', {});
global.set('zoneConflit', { occupee: false, robot: null });
global.set('phase', 'A');
global.set('listePresence', []);
node.status({ fill: 'green', shape: 'dot', text: 'Mode: ' + msg.payload });
return msg;

var phase = global.get('phase') || 'A';
var robots = global.get('robots') || {};
var mode = global.get('mode') || 'feu';

var voieGauche = 'STOP';
var voieDroite = 'STOP';

if (phase === 'A') {
    voieGauche = 'GO';
    voieDroite = 'STOP';
} else if (phase === 'B') {
    voieGauche = 'STOP';
    voieDroite = 'GO';
}

var listeRobots = [];
for (var id in robots) {
    var r = robots[id];
    if (r.actif) {
        listeRobots.push(id + ': voie ' + (r.voie || '?') + ' (' + (r.zone || '?') + ') dist:' + (r.distance || 0));
    }
}

return [
    { payload: phase + ' (' + mode + ')' },
    { payload: voieGauche },
    { payload: voieDroite },
    { payload: listeRobots.length > 0 ? listeRobots.join(' | ') : 'Aucun robot actif' }
];
